import axios from 'axios';
import EdificioCard from 'components/EdificioCard';
import Pagination from 'components/Pagination';
import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { Edificio } from 'types/edificio';
import { AxiosParams } from 'types/vendor/axios';
import { SpringPage } from 'types/vendor/spring';
import { BASE_URL } from 'utils/requests';

import './styles.css';

const Catalogo = () => {
  /*
  useState para a paginação de edificios, para receber uma página do backend e depois poder atualizá-la;
  a SpringPage <T> do tipo genérico, que terá uma lista [], que neste caso vai receber o tipo Edificio, permite "casar" a info que nos chega do Backend, 
  com a info que vamos tratar e mostrar no Frontend;
  useEffect para efetuar a requisição somente quando entramos na página.
  */
  const [page, setPage] = useState<SpringPage<Edificio>>();

  useEffect(() => {
    const params: AxiosParams = {
      method: 'GET',
      url: `${BASE_URL}/edificios`,
      params: {
        page: 0,
        size: 12,
      },
    };

    axios(params).then((response) => {
      setPage(response.data);
      console.log(page);
    });
  }, []);

  const edificio = {
    id: 10,
    localizacao: 'Aveiro',
    tipologia: 'Prédio',
    nome: 'Edificio da Areia',
    utilizacao: 'Habitação',
    arquitetura: 'Moderna',
    piso: 5,
    fracao: '1B',
    imgUrl:
      'https://www.ferreiralapa.com/wp-content/uploads/2021/09/3-1-1024x768.jpg',
    date: '2022-04-14T10:00:00Z',
    createdAt: '2022-11-09T16:17:01.672800Z',
    anomalias: [],
  };

  return (
    <div className="container my-4 catalog-container">
      <div className="row catalog-title-container">
        <h1>Edifícios intervencionados</h1>
      </div>

      <div className="row">
        <div className="col-sm-6 col-lg-4 col-xl-6">
          <Link to="edificios/1">
            <EdificioCard edificio={edificio} />
          </Link>
        </div>
        <div className="col-sm-6 col-lg-4 col-xl-6">
          <Link to="edificios/1">
            <EdificioCard edificio={edificio} />
          </Link>
        </div>
        <div className="col-sm-6 col-lg-4 col-xl-6">
          <Link to="edificios/1">
            <EdificioCard edificio={edificio} />
          </Link>
        </div>
        <div className="col-sm-6 col-lg-4 col-xl-6">
          <Link to="edificios/1">
            <EdificioCard edificio={edificio} />
          </Link>
        </div>
      </div>

      <div className="row">
        <Pagination />
      </div>
    </div>
  );
};

export default Catalogo;
