import { AxiosRequestConfig } from 'axios';
import { useForm } from 'react-hook-form';
import { Edificio } from 'types/edificio';
import { requestBackend } from 'util/requests';

import './styles.css';

const Form = () => {
  /**
   * O tipo customizado, Edificio, e os seus atributos, é o que vai controlar este formulário
   */

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm<Edificio>();

  const onSubmit = (formData: Edificio) => {
    
    const config: AxiosRequestConfig = {
      method: 'POST',
      url: '/edificios',
      data: formData,
      withCredentials: true,
    };

    requestBackend(config).then((response) => {
      console.log(response.data);
    });
  };

  return (
    <div className="edificio-crud-container">
      <div className="base-card edificio-crud-form-card">
        <h1 className="edificio-crud-form-title">Dados do Edificio</h1>

        <form onSubmit={handleSubmit(onSubmit)}>
          <div className="row edificio-crud-inputs-container">
            <div className="col-lg-6 edificio-crud-inputs-left-container">
              <div className="margin-bottom-30">
                <input
                  {...register('nome', {
                    required: 'Campo obrigatório',
                  })}
                  type="text"
                  className={`form-control base-input ${
                    errors.nome ? 'is-invalid' : ''
                  }`}
                  placeholder="Nome/designação do edifício"
                  name="nome"
                />
                <div className="invalid-feedback d-block">
                  {errors.nome?.message}
                </div>
              </div>

              <div className="margin-bottom-30">
                <input
                  {...register('nome', {
                    required: 'Campo obrigatório',
                  })}
                  type="text"
                  className={`form-control base-input ${
                    errors.nome ? 'is-invalid' : ''
                  }`}
                  placeholder="Nome/designação do edifício"
                  name="nome"
                />
                <div className="invalid-feedback d-block">
                  {errors.nome?.message}
                </div>
              </div>
              
            </div>
            <div className="col-lg-6">
              <div>
                <textarea
                  name=""
                  rows={10}
                  className="form-control base-input h-auto"
                ></textarea>
              </div>
            </div>
          </div>
          <div className="edificio-crud-buttons-container">
            <button className="btn btn-outline-danger edificio-crud-button">
              CANCELAR
            </button>
            <button className="btn btn-primary edificio-crud-button text-white">
              GRAVAR
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Form;
