import { useForm } from 'react-hook-form';
import ButtonIcon from 'components/ButtonIcon';
import { requestBackend } from 'util/requests';
import { AxiosRequestConfig } from 'axios';
import { useHistory } from 'react-router-dom';
import { toast } from 'react-toastify';

import './styles.css';


type FormData = {
  username: string;
  password: string;
  lastName: string;
  firstName: string;
  contact: string;
};

const RegisterUser = () => {

  const { register, handleSubmit } = useForm<FormData>();

  const history = useHistory();

  const onSubmit = (formData : FormData) => {
    //console.log(formData);
    const config: AxiosRequestConfig = {
      method: 'POST',
      url: '/users',
      data: formData,
    };

    requestBackend(config)
    .then(() => {
      toast.info('Utilizador registado com sucesso!')
      history.push('/admin/auth/login');
    })
    .catch(() => {
      toast.error('Erro ao registar Utilizador')
    });
  };
 
  return (
    <div className="base-card register-user-card">
      <h1>DADOS PARA REGISTO</h1>
      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="mb-4">
          <input
            {...register('username')}
            type="text"
            className="form-control base-input"
            placeholder="Email"
            name="username"
          />
        </div>
        <div className="mb-4">
          <input
            {...register('password')}
            type="password"
            className="form-control base-input "
            placeholder="Password"
            name="password"
          />
        </div>
        <div className="mb-4">
          <input
            {...register('firstName')}
            type="text"
            className="form-control base-input"
            placeholder="Nome"
            name="firstName"
          />
        </div>
        <div className="mb-4">
          <input
            {...register('lastName')}
            type="text"
            className="form-control base-input"
            placeholder="Apelido"
            name="lastName"
          />
        </div>
        <div className="mb-4">
          <input
            {...register('contact')}
            type="text"
            className="form-control base-input"
            placeholder="Telefone/TelemÃ³vel"
            name="contact"
          />
        </div>
        <div className="login-submit">
          <ButtonIcon text="REGISTAR" />
        </div>
      </form>
    </div>
  );
};

export default RegisterUser;
